function create_menu($id,$cat_id,$level_start,$level_end,$level_now, $show_levels,$style,$url="",$menu="")
{
global $m_id,$selected_cats,$selected_path, $options,$clear_uri;
$m = 1;
$q = "SELECT menu_items.name as name, menu_items.id as id,menu_items.level as level, menu_items.key_name as key_name,  menu_items.link as link, menu.style as style FROM menu_items JOIN menu ON menu.id=menu_items.menu_id WHERE menu_id='$id' and level='$level_now' and cat_id='$cat_id' and show_in_menu='1' ORDER BY menu_items.position";
	$do = doquery($q);
	$total = mysql_num_rows($do);
		while ($res = mysql_fetch_assoc($do))
			{
		 $set_url="$url";
			if ($style=="")
			{
			$style = $res["style"];
			}
			$selected = "";
			$base_style = $style;
			//$style = $base_style . "_".$res['level'];
			$style_main = $style;
			$style_hover = $style . "_hover";
				if (in_array($res["id"],$selected_cats))
				{
				$selected = "selected";
				$style_main = $style . "_selected";
				$style_hover = $style_main;
				$selected_path = "$set_url$res[key_name]";
				}
				$style_add = "";
			if ($m==1) $style_add = " first";
			if ($m==$total) $style_add = " last";
			if ($options['rewrite_url']==0)
					{
				$link = "$options[site_url]index.php/$res[key_name]";
				$link_s = "$options[site_url]index.php/$res[key_name]";
				$set_url = "";
					}
				else
					{
			$set_url = $set_url."$res[key_name]/";
					$link = "$options[site_url]$set_url";
					$link_s = "$options[site_url]$set_url";

					}
					$target_href="";
					$target_chk ="";
					if ($res["link"]!="")
						{
							$target = explode("::",$res["link"]);

						if (isset($target[1]))
							{
								$target_chk ="target='_blank'";

								$res["link"] = $target[0];
								$target_href ="window.open('$res[link]'); return false;";
							}

						$link = $res["link"];
				$link_s = $res["link"];

				if ($clear_uri==$link or $clear_uri=="$options[site_url]".$link )
								{
								$selected = "selected";
                                   $style_main = $style . "_selected";
									$style_hover = $style_main;
									$selected_path = "$set_url$res[key_name]";
									}

						}
						if ($target_href=="")
							{
								$target_href ="location.href='$link_s';";
							}

							if ($res['id']==$options['index_id'])
								{
								$link = "$options[site_url]";
								}
				$menu .= "<li class='$selected $style_add'><span class='l'></span><span class='c'><a href=\"$link\" $target_chk>$res[name]</a></span><span class='r'></span></li>";
					if ($m!=$total)
					{
					$style_add = "_divider";
					$menu .= "<li class='divider'></li>
					";
					}
			$m++;

					if ($level_end!=$level_now and (in_array($res["id"],$selected_cats) or $level_now<$show_levels))
						{

						$level_next = $res["level"]+1;
						//$new_style = $style . "_".$level_next;

						$sub_menu =   create_menu($id,$res['id'],$level_start,$level_end,$level_next, $show_levels, $base_style,$set_url,"");

						if ($sub_menu!="")
							{
							$menu .= "<ul class='{$base_style}_$level_next'>$sub_menu</ul>";

                             }
						}


			}

			return $menu;

}

