function get_cat($id)
{
$query = "SELECT cat_id FROM menu_items WHERE id='$id'";
	$do = doquery($query);
		$res = mysql_fetch_assoc($do);
		return $res["cat_id"];
}

function get_selected($selected_cats, $selected)
{

	$get_cat = get_cat($selected);
	if ($get_cat=="0" or $get_cat=="")
	{
	return $selected_cats;
	}
	else
	{
	$selected_cats[] = $get_cat;
	$selected_cats = get_selected($selected_cats, $get_cat);
	}
return $selected_cats;
}


