function get_pages($pg,$total, $show_pages=5)
{
global $options, $clear_uri, $params;
$prev = $pg-1;
$next = $pg+1;
$pt = $total-1;
$ppt = $total-2;
$pages = array();
$show_pages = array();
for ($i=1; $i<=$total; $i++)
{
$pages[] = $i;
}
// Массив ссылок
$show_pages = array();

	if ($pg!=1){
         $show_pages[] = "&laquo;";
		}

// Разбили на части
$array = array_chunk($pages,5);

foreach ($array as $block){
      if (in_array($pg,$block)){
      		foreach ($block as $page_links){

	      		if ($i!="1"){
						$link = "$clear_uri"."page/$page_links/";
							}
				else {
	               $link = "$clear_uri";
					}
					if ($params!="") $link.="?$params";
    				$selected = "";
    					if ($pg==$page_links)	$selected = "class='page_selected'";
      		$show_pages[] = "<a href='$link' $selected>$page_links</a>";
      			}
      	}
      	else {
      		if ($block[0]!=1){
      		$link = "$clear_uri"."page/$block[0]/";
      			}
      		else {
                  $link = "$clear_uri";
      			}
                 if ($params!="") $link.="?$params";

          $show_pages[] = "<a href='$link' class='pages_block'>".$block[0]."-".$block[count($block)-1]."</a>";
      		}
	}
	if ($pg!=$total){
         $show_pages[] = "&raquo;";
		}

$show_pages = implode(" ", $show_pages);
return $show_pages;
}


